import React, { PureComponent } from 'react'
import request from 'superagent'
import PokemonList from './PokemonList'
import PokemonDetails from './PokemonDetails'
import PokemonSearchForm from './PokemonSearchForm'
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { Link } from "react-router-dom";
import { withStyles } from "@material-ui/core/styles";
import { connect } from 'react-redux'
import { logout } from '../actions/auth'



const styles = () => ({
  root: {
    flexGrow: 1
  },
  container: {
    padding: 24
    // flexWrap: "wrap"
  },
  paper: {
    padding: 64
  },
  user: {
    marginRight: 32
  }
});


const PokemonListContainer = withStyles(styles)(
  class extends PureComponent {

    constructor(props) {
      super(props)
      this.state = {
        pokemons: null,
        selected: null,
        favorites: null,
        favorite: false
      }
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }

    componentDidMount() {
      //Get all pokemons
      request('http://localhost:4001/pokemons')
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
        .then(() => {
          //Request 
          //UNCOMMNET TODO
          request(`http://localhost:4001/pokemons/favorites/${this.props.currentUser.userId}`)
            .then(response => {
              const favorites = response.body.favorites.map(elem => (elem.pokemonId))
              this.setState({ favorites: favorites })
            })
        })

    }

    handleChange(event) {
      this.setState({ [event.target.name]: event.target.value })
    }

    handleSubmit(event) {
      event.preventDefault()
      console.log(JSON.stringify(this.state))
      //Search pokemon
      request('http://localhost:4001/pokemons')
        .query({ name: this.state.searchKey })
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
      //Update list     
    }

    showDetails = (name) => {
      request(`http://localhost:4001/pokemons/${name}`)
        .then(response => {
          this.checkFavorite(response.body.pokemon, this.state.favorites)
          this.setState({ selected: response.body.pokemon })
        })
    }

    changeFavorite = (event) => {
      console.log("ciao   " + event.target.checked)

      const pokemonName = this.state.selected.name
      console.log("NAME " + pokemonName)
      this.setState({ favorite: event.target.checked });
      if (event.target.checked) {
        //Add element to favorites in the state
        //FIX with ...state
        const newFavoritesList = this.state.favorites
        newFavoritesList.push(this.state.selected.id)
        this.setState({ favorites: newFavoritesList })
        request
          .post(`http://localhost:4001/users/${this.props.currentUser.userId}`)
          .query({ name: pokemonName })
        // .then(response => {
        //   //FIX with ...state
        //   const newFavoritesList = this.state.favorites
        //   newFavoritesList.push(response.body.pokemonId)
        //   this.setState({ favorites: newFavoritesList })
        //})
      } else {
        //Remove element from favorites in the state
        const newFavoritesList = this.state.favorites.filter(fav => fav !== this.state.selected.id)
        this.setState({ favorites: newFavoritesList })
        request
          .del(`http://localhost:4001/users/${this.props.currentUser.userId}`)
          .query({ name: pokemonName })
        // .then(response => {
        //   const newFavoritesList = this.state.favorites.filter(fav => fav !== response.body.pokemonId)
        //   this.setState({ favorites: newFavoritesList })
        // })
      }
    }

    checkFavorite = (selected) => {
      request(`http://localhost:4001/pokemons/favorites/${this.props.currentUser.userId}`)
        .then(response => {
          const favorites = response.body.favorites.map(elem => (elem.pokemonId))
          if (favorites.includes(selected.id)) {
            this.setState({ favorite: true })
          } else {
            this.setState({ favorite: false })
          }
        })
    }


    loggingOut = () => {
      this.props.logout()
    }


    render() {

      const { classes } = this.props

      if (!this.state.pokemons) return "Loading..."

      return (
        <div>
          <Grid container className={classes.container}>

            <Grid item container direction="column" justify="space-evenly" alignItems="flex-end">
              <Grid item>
                <PokemonSearchForm handleSubmit={this.handleSubmit} handleChange={this.handleChange} />
              </Grid>

              <Grid item>
                <Grid container justify="space-evenly">
                  <PokemonList pokemons={this.state.pokemons} showDetails={this.showDetails}
                    favorites={this.state.favorites} />
                </Grid>
              </Grid>

            </Grid>

            <Grid item container direction="column">
              <Grid item>
                <Grid container alignItems="baseline">
                  <Grid item className={classes.user}>
                    <Typography variant="h3">
                      {this.props.currentUser && this.props.currentUser.username}
                    </Typography>
                  </Grid>
                  <Grid item>
                    <Link to="/login">
                      <Button type="button" onClick={this.loggingOut}>Log Out</Button>
                    </Link>
                  </Grid>
                </Grid>
              </Grid>

              <Grid item className={classes.paper}>
                <PokemonDetails selected={this.state.selected} changeFavorite={this.changeFavorite}
                  favorite={this.state.favorite} currentUser={this.props.currentUser} loggingOut={this.loggingOut} />
              </Grid>


            </Grid>
          </Grid>
        </div>)
    }
  }
)

const mapStateToProps = function (state) {
  return {
    currentUser: state.currentUser,
    error: state.login.error
  }
}

