import * as React from 'react'
import request from 'superagent'
import PokemonList from './PokemonList'
import PokemonDetails from './PokemonDetails'
import PokemonSearchForm from './PokemonSearchForm'
import Grid from "@material-ui/core/Grid";
import { withStyles } from "@material-ui/core/styles";



const styles = () => ({
  root: {
    flexGrow: 1
  }
  // container: {
  //   padding: 24,
  //   flexWrap: "wrap"
  // }
});


const PokemonListContainer = withStyles(styles)(
  class extends React.PureComponent {

    constructor(props) {
      super(props)
      this.state = {
        pokemons: null,
        selected: null,
        favorites: null,
        favorite: false
      }
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }


    componentDidMount() {
      //Get all pokemons
      request('http://localhost:4001/pokemons')
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
      //Request favorites
      request(`http://localhost:4001/pokemons/${1}/favorites`)
        .then(response => {
          const favorites = response.body.favorites.map(elem => (elem.pokemonId))
          this.setState({ favorites: favorites })
        })
    }

    handleChange = (event) => {
      console.log(JSON.stringify(event))
      this.setState({ [event.target.name]: event.target.value })
    }

    handleSubmit = (event) => {
      event.preventDefault()
      alert(this.state.name)
      //Search pokemon
      // request('http://localhost:4001/pokemons')
      //   .query({ name: this.state.name })
      //   .then(response => {
      //     this.setState({ pokemons: response.body.pokemons })
      //   })
      //Update list

    }

    show = (name) => {
      request(`http://localhost:4001/pokemons/${name}`)
        .then(response => {
          this.setState({ selected: response.body.pokemon })
        })
        .then(this.checkFavorite())
    }

    changeFavorite = (event) => {
      console.log("ciao")
      console.log(JSON.stringify(this.state.value))
      this.setState({ value: event.target.value });
    }

    checkFavorite = () => {
      if (this.state.favorites && this.state.selected && this.state.favorites.includes(this.state.selected.id)) {
        console.log("FOUND!!!!")
        this.setState({ favorite: true })
        console.log("PIPIPIOP     "+JSON.stringify(this.state))
      } else {
        this.setState({ favorite: false })
      }
    }


    render() {

      if (!this.state.pokemons) return "Loading..."

      return (
        <Grid container alignItems="center" justify="space-around">
          <Grid item>
            <PokemonSearchForm onSubmit={this.handleSubmit} onChange={this.handleChange}/>
            <PokemonList pokemons={this.state.pokemons} show={this.show} />
          </Grid>
          <Grid item>
            <PokemonDetails selected={this.state.selected} changeFavorite={this.changeFavorite}
              favorite={this.state.favorite}/>
          </Grid>
        </Grid>
      )
    }
  })

export default PokemonListContainer
