import * as React from 'react'

export default function PokemonDetails(props) {

  if (!props.selected) return "Select a Pokemon"

  return (
    <div>
      <h1>Selected Pokemon</h1>
      <div>
        {props.selected.name}
      </div>
      <div>
        <img src={props.selected.image} alt={props.selected.image} />
      </div >
      <div>
        {props.selected.weight}
      </div >
      <div>
        {props.selected.height}
      </div>

      {props.currentUser && 
      <div>
        <label>Favorite</label>
        <input
          type="checkbox"
          value="Favorite"
          checked={props.favorite}
          onChange={props.changeFavorite}
        />
      </div>}
    </div>
  )
}
