import * as React from 'react'
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
}));


export default function PokemonDetails(props) {

  const classes = useStyles();

  if (!props.selected) return "Select a Pokemon"

  return (
    <div className={classes.root}>
    <Paper className={classes.paper}>
      <Grid container spacing={2} direction="column" alignItems="center">
        <Grid item>
            <Typography gutterBottom variant="subtitle1" fontWeight="fontWeightBold">
              <strong>{props.selected.name.toUpperCase()}</strong>
          </Typography>
        </Grid>
          <Grid item className={classes.image}>
          <img className={classes.img} src={props.selected.image} alt={props.selected.image} />
        </Grid>
          <Grid item container spacing={2} direction="column">
            <Grid item container>
          Weight: {props.selected.weight}
        </Grid>
        <Grid item>
          Height: {props.selected.height}
        </Grid>
        <Grid item>
          Exp: {props.selected.base_experience}
        </Grid>

        {props.currentUser &&
          <Grid item>
            <Typography gutterBottom variant="subtitle1">
              Favorite
          </Typography>
            <input
              type="checkbox"
              value="Favorite"
              checked={props.favorite}
              onChange={props.changeFavorite}
            />
          </Grid>
        }
          </Grid>

      </Grid>
    </Paper>
</div>

