import React, { PureComponent } from 'react'
import request from 'superagent'
import PokemonList from './PokemonList'
import PokemonDetails from './PokemonDetails'
import PokemonSearchForm from './PokemonSearchForm'
import Grid from "@material-ui/core/Grid";
import { withStyles } from "@material-ui/core/styles";
import { connect } from 'react-redux'



const styles = () => ({
  root: {
    flexGrow: 1
  }
  // container: {
  //   padding: 24,
  //   flexWrap: "wrap"
  // }
});


const PokemonListContainer = withStyles(styles)(
  class extends PureComponent {

    constructor(props) {
      super(props)
      this.state = {
        pokemons: null,
        selected: null,
        favorites: null,
        favorite: false
      }
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }


    componentDidMount() {
      //Get all pokemons
      request('http://localhost:4001/pokemons')
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })

        /*
        .then(() => {
          //Request favorites
         // console.log("prova   " + this.props.currentUser.userId)
          request(`http://localhost:4001/pokemons/favorites/${this.props.currentUser.userId}`)
            //.query({ userId: this.props.currentUser.userId })
            .then(response => {
              console.log("BODY "+JSON.stringify(response.body))
              const favorites = response.body.favorites.map(elem => (elem.pokemonId))
              this.setState({ favorites: favorites })
            })
        }
        )
        */
    }

    handleChange(event) {
      this.setState({ [event.target.name]: event.target.value })
    }

    handleSubmit(event) {
      event.preventDefault()
      console.log(JSON.stringify(this.state))
      //Search pokemon
      request('http://localhost:4001/pokemons')
        .query({ name: this.state.searchKey })
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
      //Update list     
    }

    showDetails = (name) => {
      request(`http://localhost:4001/pokemons/${name}`)
        .then(response => {
          this.checkFavorite(response.body.pokemon, this.state.favorites)
          this.setState({ selected: response.body.pokemon })
        })
    }

    changeFavorite = (event) => {
      console.log("ciao   "+event.target.checked)

      const name = this.state.selected.name
      console.log("NAME "+name)
      this.setState({ favorite: event.target.checked });
      if (event.target.checked){
        request
          .post(`http://localhost:4001/users/${this.props.currentUser.userId}`)
          .query({ name: name })
          .then(response => {
            console.log("Added: "+response.body)
          })
      } else {
        request
          .del(`http://localhost:4001/users/${this.props.currentUser.userId}`)
          .query({ name: name })
          .then(response => {
            console.log("Removed: " + response.body)
          })
      }
    }

    checkFavorite = (selected) => {
      request(`http://localhost:4001/pokemons/favorites/${this.props.currentUser.userId}`)
        //.query({ userId: this.props.currentUser.userId })
        .then(response => {
          console.log("BODY " + JSON.stringify(response.body))
          const favorites = response.body.favorites.map(elem => (elem.pokemonId))
          //this.setState({ favorites: favorites })
          if (favorites.includes(selected.id)) {
            console.log("FOUND!!!!")
            this.setState({ favorite: true })
            console.log("PIPIPIOP     " + JSON.stringify(this.state.favorites))
          } else {
            this.setState({ favorite: false })
          }
        })
   //   if (this.state.favorites && this.state.selected && this.state.favorites.includes(this.state.selected.id)) {
    }


    // logginOut = () => {
    //   this.nextPath('/login')
    // }


    render() {

      if (!this.state.pokemons) return "Loading..."

      return (
        <Grid container alignItems="center" justify="space-around">
          <Grid item>
            <PokemonSearchForm handleSubmit={this.handleSubmit} handleChange={this.handleChange} />
            <PokemonList pokemons={this.state.pokemons} showDetails={this.showDetails} favorites={this.state.favorites}/>
          </Grid>
          <Grid item> 
            <PokemonDetails selected={this.state.selected} changeFavorite={this.changeFavorite}
              favorite={this.state.favorite} currentUser={this.props.currentUser}/>
          </Grid>
        </Grid>
      )
    }
  }
)
/*
  <h1>Current User: {this.props.currentUser} </h1>
  <button> Log Out </button>
*/
const mapStateToProps = function (state) {
  return {
    currentUser: state.currentUser,
    error: state.login.error
  }
}

export default connect(mapStateToProps, {})(PokemonListContainer)
