import * as React from 'react'
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(3),
    //margin: 'auto',
    maxWidth: 250,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  points: {
    paddingLeft: "10%",
    paddingRight: "10"
  }
}));


export default function PokemonDetails(props) {

  const classes = useStyles();

  if (!props.selected) return "Select a Pokemon from the list"

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2} direction="column" alignItems="center">
          <Grid item>
            <Typography gutterBottom variant="h5">
              <strong>{props.selected.name.toUpperCase()}</strong>
            </Typography>
          </Grid>
          <Grid item className={classes.image}>
            <img className={classes.img} src={props.selected.image} alt={props.selected.image} />
          </Grid>
          <Grid item container spacing={2} direction="column">
            <Grid item container justify="space-between">
              <Grid item>
                <Typography variant="h6">
                  Weight:
                  </Typography>
                  </Grid>
              <Grid item>{props.selected.weight}</Grid>
            </Grid>
            <Grid item container justify="space-between">
              <Grid item>
                <Typography variant="h6">
                Height: 
                </Typography>
                </Grid>
              <Grid item>{props.selected.height}</Grid>
            </Grid>
            <Grid item container justify="space-between">
              <Grid item>
                <Typography variant="h6">
                Exp: 
                </Typography>
                </Grid>
              <Grid item>{props.selected.base_experience}</Grid>
            </Grid>

            {props.currentUser &&
              <Grid item container justify="space-between">
                <Grid item>
                  <Typography gutterBottom variant="h6">
                    Favorite
                  </Typography>
                </Grid>
                <Grid item>
                  <input
                    type="checkbox"
                    value="Favorite"
                    checked={props.favorite}
                    onChange={props.changeFavorite}
                  />
                </Grid>
              </Grid>
            }
          </Grid>

        </Grid>
      </Paper>
    </div>

    // <div>
    //   <h1>Selected Pokemon</h1>
    //   <div>
    //     Name: {props.selected.name}
    //   </div>
    //   <div>
    //     <img src={props.selected.image} alt={props.selected.image} />
    //   </div >
    //   <div>
    //     Weight: {props.selected.weight}
    //   </div >
    //   <div>
    //     Height: {props.selected.height}
    //   </div>
    //   <div>
    //     Exp: {props.selected.base_experience}
    //   </div>


    //   {props.currentUser && 
    //   <div>
    //     <label>Favorite</label>
    //     <input
    //       type="checkbox"
    //       value="Favorite"
    //       checked={props.favorite}
    //       onChange={props.changeFavorite}
    //     />
    //   </div>}
    // </div>
  )
}
