import * as React from 'react'
import request from 'superagent'
import PokemonList from './PokemonList'
import PokemonDetails from './PokemonDetails'
import Grid from "@material-ui/core/Grid";
import { withStyles } from "@material-ui/core/styles";


const styles = () => ({
  root: {
    flexGrow: 1
  }
  // container: {
  //   padding: 24,
  //   flexWrap: "wrap"
  // }
});


const PokemonListContainer = withStyles(styles)(
  class extends React.PureComponent {

    constructor(props) {
      super(props)
      this.onClick = this.onClick.bind(this)
    }

    state = {
      pokemons: null,
      selected: null
    }


    componentDidMount() {
      request('http://localhost:4001/pokemons')
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
    }

    show(name) {
      request(`http://localhost:4001/pokemons/${name}`)
        .then(response => {
          this.setState({ selected: response.body.pokemon })
        })
    }

    render() {

      if (!this.state.pokemons) return "Loading..."

      return (
        <Grid container alignItems="center" justify="space-around">
          <Grid item>
            <PokemonList pokemons={this.state.pokemons} show={this.show} />
          </Grid>
          <Grid item>
            <PokemonDetails />
          </Grid>
        </Grid>
      )
    }
  })

export default PokemonListContainer
