import * as React from 'react'
import request from 'superagent'
import PokemonList from './PokemonList'
import PokemonDetails from './PokemonDetails'
import Grid from "@material-ui/core/Grid";
import { withStyles } from "@material-ui/core/styles";


const styles = () => ({
  root: {
    flexGrow: 1
  }
  // container: {
  //   padding: 24,
  //   flexWrap: "wrap"
  // }
});


const PokemonListContainer = withStyles(styles)(
  class extends React.PureComponent {

    // constructor(props) {
    //   super(props)
    //   this.show = this.show.bind(this)
    // }

    state = {
      pokemons: null,
      selected: null,
      favorites: null
    }


    componentDidMount() {
      //Get all pokemons
      request('http://localhost:4001/pokemons')
        .then(response => {
          this.setState({ pokemons: response.body.pokemons })
        })
      //Request favorites
      request(`http://localhost:4001/pokemons/${1}/favorites`)
        .then(response => {
          const favorites = response.body.favorites.map(elem => (elem.pokemonId))
          this.setState({ favorites: favorites })
        })
    }

    show = (name) => {
      request(`http://localhost:4001/pokemons/${name}`)
        .then(response => {
          this.setState({ selected: response.body.pokemon })
        })
      console.log(this.state.selected)
      if (this.state.favorites && this.state.favorites.includes(this.state.selected.id)) {
        console.log("FOUND!!!!")
        this.setState(prevState => ({
          selected: {                   // object that we want to update
            ...prevState.jasper,    // keep all other key-value pairs
            favorite: true       // update the value of specific key
          }
        }))
      }
    }

    changeFavorite = (event) => {
      console.log("ciao")
      console.log(JSON.stringify(this.state.value))
      this.setState({ value: event.target.value });
    }

    checkFavorite = () => {
      if(this.state.favorites && this.state.favorites.includes(this.state.selected.id)){
        console.log("FOUND!!!!")
        return true
      }
    }

    render() {

      if (!this.state.pokemons) return "Loading..."

      return (
        <Grid container alignItems="center" justify="space-around">
          <Grid item>
            <PokemonList pokemons={this.state.pokemons} show={this.show} />
          </Grid>
          <Grid item>
            <PokemonDetails selected={this.state.selected} changeFavorite={this.changeFavorite}
              checked={this.checkFavorite} />
          </Grid>
        </Grid>
      )
    }
  })

export default PokemonListContainer
